		Date d = new Date();
		System.out.println("Todays Date is: " + d);
		System.out.println("Current Date: " + d.getDate());
		System.out.println("Current Day: " + d.getDay());
		System.out.println("Current Year: " + d.getYear());
		System.out.println("Current Hours: " + d.getHours());
