import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

/*
 * Collections.indexOfSubList() method returns the starting position of the first
 * occurrence of the specified target list within the specified source list,
 * or -1 if there is no such occurrence.
*/

public class MySubListIndex {

	public static void main(String a[]) {

		List<String> list = new ArrayList<String>();
		list.add("java");
		list.add("c");
		list.add("c++");
		list.add("unix");
		list.add("perl");
		list.add("php");
		list.add("javascript");
		list.add("ruby");
		list.add(".net");
		list.add("jdbc");
		list.add("servlets");
		List<String> subList = new ArrayList<String>();
		subList.add("php");
		subList.add("javascript");
		subList.add("ruby");
		System.out.println("Index of sublist: " + Collections.indexOfSubList(list, subList));
	}
}

/*
 * Example Output
 * 
 * Index of sublist: 5
 */

/*
 * Comparison of Algorithms
 */

public class MyPerfomanceComparison {
	public static void main(String[] args) {
		concatenate(args);		
	}

	private static void concatenate(String[] args) {
		//Slow
		Stopwatch.start();
		String x = "a" + args.length + "b";
		Stopwatch.stop();

		//Faster
		Stopwatch.start();
		String y = "a" + args.length + "b";

		if (args.length == 1)
			y = y + args[0];
		Stopwatch.stop();

		//Fastest
		Stopwatch.start();
		StringBuilder z = new StringBuilder("a");
		z.append(args.length);
		z.append("b");
		if (args.length == 1)
			z.append(args[0]);		
		Stopwatch.stop();

		System.out.println(x);
		System.out.println(y);
		System.out.println(z);
	}
}



/*
 * Implementation of Time Measurement in Nanoseconds Billion
 */

class Stopwatch {

	private static long startTime;

	public static void start() {

		startTime = System.nanoTime();

	}

	public static void stop() {
		long stopTime = System.nanoTime();
		long elapsedTime = stopTime - startTime;
		System.out.println(elapsedTime + " Nanoseconds");
	}

}

/*
 * Example Output
 * 
 * 6425 Nanoseconds
 */

import java.util.Map; import java.util.Set;
/* * The ProcessBuilder.environment() method returns you the * ProcessBuilder's environemnt. Below example shows how to read it. */
public class MyEnvDetails {
public static void main(String a[]) {
ProcessBuilder pb = new ProcessBuilder(); Map<String, String> envMap = pb.environment(); Set<String> keys = envMap.keySet(); for (String key : keys) { System.out.println(key + " ==> " + envMap.get(key)); } } }
