public class MySetWithCompr {

	public static void main(String a[]) {

		TreeSet<String> ts = new TreeSet<String>(new MyComp());
		ts.add("RED");
		ts.add("ORANGE");
		ts.add("BLUE");
		ts.add("GREEN");
		System.out.println(ts);
	}
